# Initialize Sentence Transformer model
model = SentenceTransformer('all-MiniLM-L6-v2')

# Create knowledge base for RAG
def create_knowledge_base():
    knowledge_texts = []
    for product in products:
        # Basic product information
        text = f"Product: {product['name']}\n"
        text += f"Category: {product['category']}\n"
        text += f"Price: ${product['price']}\n"
        text += f"Description: {product['description']}\n"
        
        # Detailed information
        details = product['details']
        for key, value in details.items():
            if isinstance(value, list):
                text += f"{key.replace('_', ' ').title()}: {', '.join(value)}\n"
            else:
                text += f"{key.replace('_', ' ').title()}: {value}\n"
        
        knowledge_texts.append(text)
    return knowledge_texts

# Create FAISS index
knowledge_base = create_knowledge_base()
embeddings = [model.encode(text) for text in knowledge_base]
index_dimension = len(embeddings[0])
index = faiss.IndexFlatL2(index_dimension)
index.add(np.array(embeddings).astype('float32'))

def is_product_query(query):
    """Determine if the query is asking for product recommendations"""
    recommendation_keywords = ['recommend', 'show', 'suggest', 'find', 'looking for', 'search', 'want to buy']
    return any(keyword in query.lower() for keyword in recommendation_keywords)

def get_relevant_products(query, category=None, eco_friendly_only=False):
    """Get relevant products based on the query and filters"""
    query_embedding = model.encode(query).reshape(1, -1).astype('float32')
    _, indices = index.search(query_embedding, len(products))
    
    results = []
    for idx in indices[0]:
        product = products[idx]
        if category and product['category'].lower() != category.lower():
            continue
        if eco_friendly_only and not product['eco_friendly']:
            continue
        results.append(product)
    
    return results

def generate_product_response(products_list):
    """Generate a simple response with just product names"""
    if not products_list:
        return "Sorry, I couldn't find any products matching your criteria."
    
    response = "Here are the relevant products:\n"
    for product in products_list:
        response += f"- {product['name']}\n"
    return response

def answer_product_question(query, product_results):
    """Generate a response for product-specific questions"""
    query_embedding = model.encode(query).reshape(1, -1).astype('float32')
    _, indices = index.search(query_embedding, 1)
    
    most_relevant_text = knowledge_base[indices[0][0]]
    relevant_product = products[indices[0][0]]
    
    # Extract specific information based on common question types
    query_lower = query.lower()
    if 'price' in query_lower:
        return f"The {relevant_product['name']} costs ${relevant_product['price']}."
    elif 'warranty' in query_lower:
        return f"The {relevant_product['name']} comes with a {relevant_product['details']['warranty']} warranty."
    elif 'material' in query_lower or 'made' in query_lower:
        materials = ', '.join(relevant_product['details']['materials'])
        return f"The {relevant_product['name']} is made of {materials}."
    elif 'color' in query_lower:
        colors = ', '.join(relevant_product['details']['color_options'])
        return f"The {relevant_product['name']} is available in these colors: {colors}."
    else:
        # For other types of questions, return relevant information from the knowledge base
        return f"Here's what I know about the {relevant_product['name']}:\n{most_relevant_text}"

@app.route('/')
def home():
    return render_template('index.html', products=products)

@app.route('/ask', methods=['POST'])
def ask():
    user_query = request.json.get('query', '')
    
    # Extract category if mentioned
    categories = [p['category'].lower() for p in products]
    mentioned_category = next((cat for cat in categories if cat in user_query.lower()), None)
    
    # Check if query is asking for eco-friendly products
    eco_friendly_filter = 'eco' in user_query.lower() or 'sustainable' in user_query.lower()
    
    if is_product_query(user_query):
        # Handle product recommendations
        relevant_products = get_relevant_products(
            user_query, 
            category=mentioned_category,
            eco_friendly_only=eco_friendly_filter
        )
        response = generate_product_response(relevant_products)
    else:
        # Handle product questions
        relevant_products = get_relevant_products(user_query)
        response = answer_product_question(user_query, relevant_products)

    return jsonify({"answer": response})

